mapper.select:
        SELECT


--         级联表的字段

        FROM zhaopin  a



                and a.id in

                #{item}



                and a.zhaopin_name like CONCAT('%',#{params.zhaopinName},'%')


                and a.zhaopin_types = #{params.zhaopinTypes}


                  and a.zhaopin_number >= #{params.zhaopinNumberStart}   


                  and a.zhaopin_number <= #{params.zhaopinNumberEnd}   


                and a.zhaopin_number = #{params.zhaopinNumber}


                and a.zhaopin_content like CONCAT('%',#{params.zhaopinContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
